import styled from 'styled-components'

const Container = styled.div`
    margin-top: -60px;
    margin-left: 0;
    margin-right: 0;
    background: #f6f1cb;
    display: block;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    position: absolute;
    z-index: 1;
`
const SearchBox = styled.div`
    margin-top: 90px;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    background-color: #283618;
    width: 80%;
    height: 130px;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
    overflow: hidden;
`
const SearchResults = styled.div`
    width: 80%;
    height: 65%;
    margin-top: 20px;
    margin-left: auto;
    margin-right: auto;
    background: #f7f7f7;
    overflow: auto;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
`
const SearchButtons = styled.div`
    height: 20px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
    z-index: 2;

    input {
        display: none;

        &:checked + label{
            background-color: #4CAF50;
            color: white;
            border: 1px solid #4CAF50;
        }
    }


    label {
        color: #ffffff;
        float: left;
        padding: 8px 16px;
        text-decoration: none;
        border: 1px solid #f7f7f7;
        width: 40%;

        /* &:hover:not(.active) {background-color: #ddd;} */

        &:first-child {
            border-top-left-radius: 5px;
            border-bottom-left-radius: 5px;
        }
        &:last-child {
            border-top-right-radius: 5px;
            border-bottom-right-radius: 5px;
        }
    }
`
const SlideBar = styled.nav`
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: arial, sans-serif;
    max-width: 900px;
    margin: 50px auto;
    overflow-wrap: break-word;
    height: 80px;
    display: block;
    background: #fefff5;
    margin-top: 10px;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);


    input {
        display: none;

        &:checked + label{
            opacity: 1;
            color: #fff;
        }
    }

    label {
        width: 20%;
        height: 80px;
        float: left;
        color: #000;
        opacity: 0.2;
        font-size: 30px;
        display: flex;
        justify-content: center;
        align-items: center;

        &:hover{
            cursor: pointer;
            color: #666;
        }   
    }

    span{
        display: block;
        padding: 10px;
        font-size: 20px;
        font-weight: 900;
        text-align: center;
    }

    .sliderTab{
        width: 20%;
        height: 80px;
        border-radius: 5px;
        background: -webkit-linear-gradient(left, #455c2b, #283618);
    }

    label, .sliderTab {
    transition: all 500ms ease-in-out;
    -webkit-transition: all 500ms ease-in-out;
    -moz-transition: all 500ms ease-in-out;
    }

    .item1{ &:checked ~ .sliderTab{ margin-left: 0%;} }
    .item2{ &:checked ~ .sliderTab{ margin-left: 20%;} }
    .item3{ &:checked ~ .sliderTab{ margin-left: 40%;} }
    .item4{ &:checked ~ .sliderTab{ margin-left: 60%;} }
    .item5{ &:checked ~ .sliderTab{ margin-left: 80%;} }
`


const SearchElements = () => {
    return (
        <Container>
            <SearchBox>
                <SearchButtons>
                    <input type="radio" name="buttons" id="item1" defaultChecked/>
                    <label htmlFor="item1" >Basic Search</label>
                    <input type="radio" name="buttons" id="item2" defaultChecked/>
                    <label htmlFor="item2" >Advanced Search: Item</label>
                    <input type="radio" name="buttons" id="item3" defaultChecked/>
                    <label htmlFor="item3">Advanced Search: Tobacco</label>

                </SearchButtons>
            </SearchBox>
            <SearchResults/>
        </Container>
    )
}

export default SearchElements
