import React, { useState } from 'react'
import Navbar from '../components/Navbar.js'
import HomeSection from '../components/HomeSection.js'
import NavSidebar from '../components/NavSidebar.js'
import LoginSignUpForm from '../components/LoginSignUpForm.js'
import HomeHeader from '../components/HomeHeader.js'
import AboutSection from '../components/AboutSection.js'
// import { sectionObject } from '../components/Data.js'
import { createGlobalStyle } from 'styled-components'

const GlobalStyle = createGlobalStyle`

    body{
        background-color: #f6f1cb;
        flex:1;
    }
`

const Home = () => {
    const[isOpen, setIsOpen] = useState(false)
    const toggle = () => {
        setIsOpen(!isOpen)
    }
  return (
    <GlobalStyle>
        <NavSidebar isOpen={isOpen} toggle={toggle} />
        <Navbar toggle={toggle} />
        <HomeHeader/>
        <LoginSignUpForm/>
        <HomeSection/> 
        <AboutSection/>
    </GlobalStyle>
  )
}

export default Home
