import styled from 'styled-components'

const Container = styled.div`
    margin-top: -60px;
    margin-left: 0;
    margin-right: 0;
    background: #f6f1cb;
    display: block;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    position: absolute;
    z-index: 1;
`
const SearchBox = styled.div`
    margin-top: 90px;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    position: relative;
    background-color: #283618; 
    /* 283618; */
    width: 80%;
    height: 150px;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
    overflow: hidden;

    input {
        border-radius: 5px;
        position: absolute;

    }
`
const SearchResults = styled.div`
    width: 80%;
    height: 65%;
    margin-top: 20px;
    margin-left: auto;
    margin-right: auto;
    background: #f7f7f7;
    overflow: auto;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
`
const SearchButtons = styled.div`
    height: 30px;
    padding: 0;
    width: 75%;
    position: absolute;
    top: 20px;
    left: 0;
    right: 0;
    margin-left: auto;
    margin-right: auto;
    box-sizing: border-box;
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 2; 

    input {
        display: none;

        &:checked + label{
            background-color: #b8ac4f;
            color: white;
            /* border: 1px solid #000000; */
        }
    }

    label {
        color: #ffffff;
        background: #51633d;
        float: left;
        height: 20px;
        text-decoration: none;
        text-align: center;
        border: 2px solid #000000;
        font-size: 15px;
        font-weight: bold;
        width: 33%;
        display: flex;
        justify-content: center;
        align-items: center;
        overflow: hidden;

        &:hover:not(.active) {
            background-color: #ddd;
            color: #000000;
        }

        &:nth-child(2) {
            border-radius: 5px;
            margin-right: 10px;
            box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2);
        }
        &:nth-child(4) {
            border-top-left-radius: 5px;
            border-bottom-left-radius: 5px;
            box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2);
            clip-path: inset(-20px 0px -20px -20px);
        }
        &:last-child {
            border-top-right-radius: 5px;
            border-bottom-right-radius: 5px;
            box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2);
            clip-path: inset(-20px -20px -20px 0);
        }
    }
`
const ButtonLabels = styled.div`
    height: 100px;
    padding: 0;
    width: 75%;
    margin-left: auto;
    margin-right: auto;
    box-sizing: border-box;
    z-index: 2; 
    display: grid;
    grid-auto-columns: minmax(auto, 1fr);
    grid-template-areas: 'col1 col2 col2';

    span {
        color: #ffffff;
        height: 20px;
        text-align: center;
        font-size: 15px;
        font-weight: bold;
        z-index: 4;

    }

    .box{
        grid-area: col2;
        height: 30px;
        border: 3px solid #ffffff;
        border-radius: 5px;
        margin-top: 17px;
        margin-right: -5px;
        margin-left: 2px;
        box-sizing: content-box;
        justify-content: center;
        align-items: center;
        z-index: 3;
    }
`


const SearchElements = () => {
    return (
        <Container>
            <SearchBox>
                <ButtonLabels>
                    <div className='box'/>
                    <span style={{gridArea: "col2"}}>Advanced Search</span>
                </ButtonLabels>
                <SearchButtons>
                    <input type="radio" name="buttons" id="item1" defaultChecked/>
                    <label htmlFor="item1" >Basic Search</label>
                    <input type="radio" name="buttons" id="item2" />
                    <label htmlFor="item2" class='share'>Item</label>
                    <input type="radio" name="buttons" id="item3" />
                    <label htmlFor="item3"  class='share'>Tobacco</label>
                </SearchButtons>
                <input placeholder='Search'/>
            </SearchBox>
            <SearchResults/>
        </Container>
    )
}

export default SearchElements
