import React from 'react'
import styled from 'styled-components'
import { ScrollButton } from './AllElements'
import aboutImage from '../images/aboutImage.jpeg'

const InfoContainer = styled.div`
    color: #000000;
    background: #f6f1cb;
;

    @media screen and (max-width: 786px) {
        padding: 100px 0;
    }
`
const InfoWrapper = styled.div`
    display: grid;
    z-index: 1;
    height: 860px;
    width: 100%;
    max-width: fit-content;
    margin-right: auto;
    margin-left: auto;
    padding: 0 24px;
    justify-content: center;
`
const InfoRow = styled.div`
    display: grid;
    grid-auto-columns: minmax(auto, 1fr);
    grid-template-areas: 'col0 col0' 'col1 col2' ;

    @media screen and (max-width: 786px) {
        grid-template-areas: 'col0' 'col1' 'col2';
    }
`
const Column0 = styled.div`
    text-align: center;
    padding: 30px 15px;
    grid-area: col0;
    position: relative;
`
const Column1 = styled.div`
    margin-bottom: 15px;
    padding: 0 15px;
    grid-area: col1;
`
const Column2 = styled.div`
    margin-bottom: 15px;
    padding: 0 15px;
    grid-area: col2;
`
const TextWrapper = styled.div`
    width: 100%;
    padding-top: 0;
    padding-bottom: 60px;
`
const TopLine = styled.p`
    color: #01bf71;
    font-size: 16px;
    line-height: 16px;
    font-weight: 700;
    letter-spacing: 1.4px;
    text-transform: uppercase;
    margin-bottom: 16px;
`
const Heading = styled.h1`
    font-size: 48px;
    line-height: 1.1;
    font-weight: 600;
    color: #010606;

    @media screen and (max-width: 480px) {
        font-size: 32px;
    }    
`
const Subtitle = styled.p`
    width: 100%;
    font-size: 18px;
    line-height: 24px;
    color: #000000;
`
const ImageWrap = styled.div`
    width: 100%;
    height: 100%;
`
const Image = styled.img`
    width: 100%;
    /* margin: 0 0 10px 0; */
    padding-right: 0;
`
const slideBar = styled.div`

    input {
        display: none;
    }

    label {
        width: 20%;
        height: 80px;
        float: left;
        color: #000;
        opacity: 0.2;
        font-size: 30px;
        display: flex;
        justify-content: center;
        align-items: center;

        &:hover{
            cursor: pointer;
            color: #666;
        }   

        span{
            display: block;
            padding: 10px;
        }
    }

    .sliderTab{
        width: 20%;
        height: 80px;
        background: #333;
        border-radius: 5px;
    }

    label, .sliderTab {
    transition: all 500ms ease-in-out;
    -webkit-transition: all 500ms ease-in-out;
    -moz-transition: all 500ms ease-in-out;
    }

    input:checked + label{
        opacity: 1;
        color: #fff;
    }

    .item1{ &:checked ~ .sliderTab{ margin-left: 0%;} }
    .item2{ &:checked ~ .sliderTab{ margin-left: 20%;} }
    .item3{ &:checked ~ .sliderTab{ margin-left: 40%;} }
    .item4{ &:checked ~ .sliderTab{ margin-left: 60%;} }
    .item5{ &:checked ~ .sliderTab{ margin-left: 90%;} }
`
const textContainer = styled.div`
    font-size: 20px;
    text-align: center;
`


const AboutSection = () => {
    return (
        <>
        <InfoContainer id='about'>

            <slideBar>
                <input type="radio" className="item1" id="item1" checked/>
                <label for="item1" ><span><textContainer>History Revealed</textContainer></span></label>

                <input type="radio" className="item2" id="item2"/>
                <label for="item2" ><span><textContainer>The Project</textContainer></span></label>

                <input type="radio" className="item3" id="item3"/>
                <label for="item3" ><span><textContainer>Transcription and the Database</textContainer></span></label>

                <input type="radio" className="item4" id="item4"/>
                <label for="item4" ><span><textContainer>The Ledgers</textContainer></span></label>

                <input type="radio" className="item5" id="item5"/>
                <label for="item5" ><span><textContainer>Support and Volunteers</textContainer></span></label>

                <div class="sliderTab"/>
            </slideBar>
        </InfoContainer>    
        </>
    );
};

export default AboutSection;
