import styled from 'styled-components'

const Container = styled.div`
    margin-top: -60px;
    margin-left: 0;
    margin-right: 0;
    background: #f6f1cb;
    display: block;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    position: absolute;
    z-index: 1;
`
const SearchBox = styled.div`
    margin-top: 90px;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    background-color: #283618;
    width: 80%;
    height: 130px;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
    overflow: hidden;
`
const SearchResults = styled.div`
    width: 80%;
    height: 65%;
    margin-top: 20px;
    margin-left: auto;
    margin-right: auto;
    background: #f7f7f7;
    overflow: auto;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
`
const SearchButtons = styled.div`
    height: 30px;
    padding: 0;
    width: 75%;
    position: absolute;
    top: 150;
    box-sizing: border-box;
    margin-left: auto;
    margin-right: auto;
    display: block;
    justify-content: center;
    background: #000000;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
    z-index: 2; 

    input {
        display: none;

        &:checked + label{
            background-color: #b8ac4f;
            color: white;
            border: 1px solid #e2d571;
        }
    }

    label {
        color: #ffffff;
        float: left;
        height: 40px;
        text-decoration: none;
        text-align: center;
        border: 1px solid #f7f7f7;
        font-size: 20px;
        font-weight: bold;
        width: 33%;
        display: flex;
        justify-content: center;
        align-items: center;

        &:hover:not(.active) {background-color: #ddd;}

        &:nth-child(2) {
            border-top-left-radius: 5px;
            border-bottom-left-radius: 5px;
        }
        &:last-child {
            border-top-right-radius: 5px;
            border-bottom-right-radius: 5px;
        }
    }
`
const ButtonLabels = styled.div`
    height: 40px;
    padding: 0;
    width: 75%;
    position: absolute;
    top: 50px;
    left: auto;
    right: auto;
    box-sizing: border-box;
    margin-left: auto;
    margin-right: auto;
    display: block;
    justify-content: center;
    background: #000000;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
    z-index: 2; 

    span {
        color: #ffffff;
        float: left;
        height: 40px;
        text-decoration: none;
        text-align: center;
        font-size: 20px;
        font-weight: bold;
        width: 33%;
        display: flex;
        justify-content: center;
        align-items: center;
    }
`


const SearchElements = () => {
    return (
        <Container>
            <SearchBox>
                <ButtonLabels>
                    <span>Basic</span>
                    <span>Advanced</span>
                </ButtonLabels>
                <SearchButtons>
                    <input type="radio" name="buttons" id="item1" defaultChecked/>
                    <label htmlFor="item1" >Basic Search</label>
                    <input type="radio" name="buttons" id="item2" defaultChecked/>
                    <label htmlFor="item2" >Advanced: Item</label>
                    <input type="radio" name="buttons" id="item3" defaultChecked/>
                    <label htmlFor="item3">Advanced: Tobacco</label>
                </SearchButtons>
            </SearchBox>
            <SearchResults/>
        </Container>
    )
}

export default SearchElements
