import React from 'react'
import styled from 'styled-components'
import { ScrollButton } from './AllElements'
import aboutImage from '../images/aboutImage.jpeg'

const InfoContainer = styled.div`
    color: #ffffff;
    background: ${({lightBg}) => (lightBg ? '#f9f9f9' : '#010606')};

    @media screen and (max-width: 786px) {
        padding: 100px 0;
    }
`
const InfoWrapper = styled.div`
    display: grid;
    z-index: 1;
    height: 860px;
    width: 100%;
    max-width: 1100px;
    margin-right: auto;
    margin-left: auto;
    padding: 0 24px;
    justify-content: center;
`
const InfoRow = styled.div`
    display: grid;
    grid-auto-columns: minmax(auto, 1fr);
    align-items: center;
    grid-template-areas: ${({imgStart}) => (imgStart ? `'col2 col1'`: `'col1 col2'`)};

    @media screen and (max-width: 786px) {
        grid-template-areas: ${({imgStart}) => (
            imgStart ? `'col1' 'col2'`: `'col1 col1' 'col2 col2'`
            )};
    }
`
const Column1 = styled.div`
    margin-bottom: 15px;
    padding: 0 15px;
    grid-area: col1;
`
const Column2 = styled.div`
    margin-bottom: 15px;
    padding: 0 15px;
    grid-area: col2;
`
const TextWrapper = styled.div`
    max-width: 540px;
    padding-top: 0;
    padding-bottom: 60px;
`
const TopLine = styled.p`
    color: #01bf71;
    font-size: 16px;
    line-height: 16px;
    font-weight: 700;
    letter-spacing: 1.4px;
    text-transform: uppercase;
    margin-bottom: 16px;
`
const Heading = styled.h1`
    margin-bottom: 24px;
    font-size: 48px;
    line-height: 1.1;
    font-weight: 600;
    color: ${({lightText}) => (lightText ? `#f7f8fa`: `#010606`)};

    @media screen and (max-width: 480px) {
        font-size: 32px;
    }    
`
const Subtitle = styled.p`
    max-width: 440px;
    margin-bottom: 35px;
    font-size: 18px;
    line-height: 24px;
    color: ${({darkText}) => (darkText ? `#010606`: `#ffffff`)};
`
const ButtonWrap = styled.div`
    display: flex;
    justify-content: flex-start;
`
const ImageWrap = styled.div`
    max-width: 555px;
    height: 100%;
`
const Image = styled.img`
    width: 100%;
    margin: 0 0 10px 0;
    padding-right: 0;
`
const AboutSection = () => {
    return (
        <>
        <InfoContainer id='about'>
            <InfoWrapper>
                <InfoRow>
                    <Column1>
                        <TextWrapper>
                            <TopLine></TopLine>
                            <Heading ></Heading>
                            <Subtitle ></Subtitle>
                            <ButtonWrap>
                                <ScrollButton to='home'></ScrollButton>
                            </ButtonWrap>
                        </TextWrapper>
                    </Column1>
                    <Column2>
                        <ImageWrap>
                            <Image src={aboutImage}/>
                        </ImageWrap>
                    </Column2>
                </InfoRow>
            </InfoWrapper>
        </InfoContainer>    
        </>
    );
};

export default AboutSection;
