import React from 'react'
import {useState, useRef} from 'react'
import userPool from '../UserPool';
import styled from 'styled-components';
import 'typeface-titillium-web'
import { CognitoUserAttribute, CognitoUser, AuthenticationDetails} from "amazon-cognito-identity-js";
import UserPool from '../UserPool';

const FullContainer = styled.div`
    margin: 0;
    box-sizing: border-box;
    font-family: 'Titillium Web', sans-serif;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    padding-top: 230px;
    padding-left: 90px;

    align-items: center;
    z-index: 2;
`
const Wrapper = styled.div`
    margin: 0;
    box-sizing: border-box;
    overflow: hidden;
    max-width: 390px;
    background: #f6f1cb;
    padding: 30px;
    border-radius: 5px;
    height: ${(props) => props.height};
    /* box-shadow: 0px 15px 20px rgba(0,0,0,0.1); */
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
    place-items: center;
`
const TitleText = styled.div`
    margin: 0;
    box-sizing: border-box;
    display: flex;
    width: 200%;
    ${props => (props.$mode !== "login" ? `margin-left: 0%;` : 'margin-left: -50%;')}

`
const Title = styled.div`
    margin: 0;
    box-sizing: border-box;
    width: 50%;
    font-size: 40px;
    font-weight: 700;
    text-align: center;
    transition: all 0.6s cubic-bezier(0.68,-0.55,0.265,1.55);
`
const SlideControls = styled.div`
    margin: 0;
    box-sizing: border-box;
    position: relative;
    display: flex;
    height: 50px;
    width: 100%;
    overflow: hidden;
    background: #ffffff;
    margin: 20px 0 10px 0;
    justify-content: space-between;
    border: 1.5px solid lightgray;
    border-radius: 5px;

    .slide{
        height: 100%;
        width: 100%;
        color: #fff;
        font-size: 18px;
        font-weight: 500;
        text-align: center;
        line-height: 48px;
        cursor: pointer;
        z-index: 1;
        transition: all 0.6s ease;
    }
    .signuplabel {
        color: #000;
    }
    .loginLabel {}

    .sliderTab{
        position: absolute;
        height: 100%;
        width: 50%;
        left: 0;
        z-index: 0;
        border-radius: 5px;
        background: -webkit-linear-gradient(left, #455c2b, #283618);
        transition: all 0.6s cubic-bezier(0.68,-0.55,0.265,1.55);
    }
    .signUpInput {
        display: none;

        &:checked ~ .loginLabel {
            color: #000;
        }
        &:checked ~ .signuplabel {
            color: #fff;
            font-weight: bolder;
            cursor: default;
            user-select: none;
        }
        &:checked ~ .sliderTab{
            left: 50%;
        }


    }

    .loginInput {
        display: none;
        &:checked ~ .signuplabel{
            color: #000;    
        }
        &:checked ~ .loginLabel{
            font-weight: bolder;
            cursor: default;
            user-select: none;
        }

    }

`
const FormContainer = styled.div`
    margin: 0;
    box-sizing: border-box;
    width: 100%;
    overflow: hidden;
`
const FormInner = styled.div`
    margin: 0;
    box-sizing: border-box;
    display: flex;
    width: 200%;
`
