const LoginSignUpForm = () => {
    const [given_name, setFirstName] = useState('');
    const [family_name, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [confirmEmail, setConfirmEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const handleFirstName = (e) => { setFirstName(e.target.value); }
    const handleLastName = (e) => { setLastName(e.target.value); }
    const handleEmail = (e) => { setEmail(e.target.value); }
    const handleConfirmEmail = (e) => { setConfirmEmail(e.target.value); }
    const handlePassword = (e) => { setPassword(e.target.value); }
    const handleConfirmPassword = (e) => { setConfirmPassword(e.target.value); }

    const submitSignup = (e) => {
        if (password !== confirmPassword) {console.log("password Not Match");} 
        else if (email !== confirmEmail) {console.log("password Not Match");} 

        const attributeList = [
            new CognitoUserAttribute({ Name: 'email', Value: email }),
            new CognitoUserAttribute({ Name: 'given_name', Value: given_name }),
            new CognitoUserAttribute({ Name: 'family_name', Value: family_name })
        ]

        e.preventDefault();

        userPool.signUp(email, password, attributeList, null, (err,data) =>{
            if (err) {
                console.error(err);
            }
            setFirstName("")
            setLastName("")
            setEmail("")
            setConfirmEmail("")
            setPassword("")
            setConfirmPassword("")

            console.error(data);
        });
    }

    const submitLogin = (e) => {
        e.preventDefault();

        const user = new CognitoUser({
            Username: email,
            Pool: UserPool
        });

        const authDetails = new AuthenticationDetails({
            Username: email,
            Password: password
        })

        user.authenticateUser(authDetails, {
            onSuccess: (data) => {
                console.log("onSuccess:", data)
            },
            onFailure: (err) => {
                console.error("onFailure:", err)
            }, 
            newPasswordRequired: (data) => {
                console.log("newPasswordRequired:", data)
            }
        });
    };

    // Changes the form bewtween Login and Sign Up
    const [page, setPage] = useState("0%");
    const [height_, setHeight] = useState(["480px", "50px", "20px"]);
    const signInRef = useRef(null)

    return (
        <FullContainer>
                <Wrapper height={height_[0]} >
                    <TitleText >
                        <Title style ={{marginLeft: page}}>
                            Login Form
                        </Title>
                        <Title>
                            Signup Form
                        </Title>
                    </TitleText>
                    <FormContainer>
                        <SlideControls>
                            <input type="radio" className='loginInput' name="slide" id="login" onClick={(e) => {setPage("0%"); setHeight(["480px","50px", "20px"])}} defaultChecked/>
                            <input type="radio" className='signUpInput' name="slide" id="signup" ref={signInRef} onClick={(e) => {setPage("-50%"); setHeight(["580px","40px","10px"])}}/>
                            <label htmlFor="login" className="slide loginLabel" id="login">Login</label>
                            <label htmlFor="signup" className="slide signuplabel" id="signup">Signup</label>
                            <div className="sliderTab"></div>
                        </SlideControls>
                        <FormInner>
                            <FormInnerForm action="#" style={{marginLeft: page}}>
                                <FormField height={height_[1]} margintop={height_[2]}>
                                    <FormFieldInput type="text" placeholder="Email Address" required />
                                </FormField>
                                <FormField height={height_[1]} margintop={height_[2]}>
                                    <FormFieldInput type="password" placeholder="Password" required />
                                </FormField>
                                <PassLink>
                                    <PassLinkA href="google.com">Forgot password?</PassLinkA>
                                </PassLink>
                                <FormButton>
                                    <ButtonLayer></ButtonLayer>
                                    <FormButtonInput type="submit" value="Login" onClick={submitLogin}/>
                                </FormButton>
                                <SignUpLink>
                                    Not a member? <SignUpLinkA onClick={(e) => signInRef.current.click()}>Signup Now</SignUpLinkA>
                                </SignUpLink>
                            </FormInnerForm>
                            <FormInnerForm action="#">
                                <FormField height={height_[1]} margintop={height_[2]}>
                                    <FormFieldInput type="text" value={given_name} placeholder="First Name" required onChange={handleFirstName}/>
                                </FormField>
                                <FormField height={height_[1]} margintop={height_[2]}>
                                    <FormFieldInput type="text" value={family_name} placeholder="Last Name" required onChange={handleLastName}/>
                                </FormField>
                                <FormField height={height_[1]} margintop={height_[2]}>
                                    <FormFieldInput type="text" value={email} placeholder="Email Address" required onChange={handleEmail}/>
                                </FormField>
                                <FormField height={height_[1]} margintop={height_[2]}>
                                    <FormFieldInput type="text" value={confirmEmail} placeholder="Confirm Email Address" required onChange={handleConfirmEmail} />
                                </FormField>
                                <FormField height={height_[1]} margintop={height_[2]}>
                                    <FormFieldInput type="password" value={password} placeholder="Password" required onChange={handlePassword}/>
                                </FormField>
                                <FormField height={height_[1]} margintop={height_[2]}>
                                    <FormFieldInput type="password" value={confirmPassword} placeholder="Confirm password" required onChange={handleConfirmPassword}/>
                                </FormField>
                                <FormButton>
                                    <ButtonLayer></ButtonLayer>
                                    <FormButtonInput type="submit" value="Signup" onClick={submitSignup}/>
                                </FormButton>
                            </FormInnerForm>
                        </FormInner>
                    </FormContainer>
                </Wrapper>
        </FullContainer> 
    );
};

