import styled from 'styled-components'

const Container = styled.div`
    margin-top: -60px;
    margin-left: 0;
    margin-right: 0;
    background: #f6f1cb;
    display: block;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    position: absolute;
    z-index: 1;
`
const SearchBox = styled.div`
    margin-top: 90px;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    background-color: #283618;
    width: 80%;
    height: 130px;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
    overflow: hidden;
`
const SearchResults = styled.div`
    width: 80%;
    height: 65%;
    margin-top: 20px;
    margin-left: auto;
    margin-right: auto;
    background: #f7f7f7;
    overflow: auto;
    border-radius: 5px;
    box-shadow: 0 4px 8px 5px rgba(0, 0, 0, 0.2), 0 15px 20px 5px rgba(0, 0, 0, 0.19);
`
const SearchButtons = styled.div`
    display: inline-block;
    z-index: 2;
    a {
        color: black;
        float: left;
        padding: 8px 16px;
        text-decoration: none;
        border: 1px solid #ddd;

        .active {
            background-color: #4CAF50;
            color: white;
            border: 1px solid #4CAF50;
        }

        &:hover:not(.active) {background-color: #ddd;}

        &:first-child {
            border-top-left-radius: 5px;
            border-bottom-left-radius: 5px;
        }
        &:last-child {
            border-top-right-radius: 5px;
            border-bottom-right-radius: 5px;
        }
    }
`



const SearchElements = () => {
    return (
        <Container>
            <SearchBox>
                <SearchButtons>
                    <a href="#">1</a>
                    <a href="#">3</a>
                    <a href="#">4</a>
                </SearchButtons>
            </SearchBox>
            <SearchResults/>
        </Container>
    )
}

export default SearchElements
