import React from 'react'
import styled from 'styled-components';
// import {FullContainer, Wrapper, FormContainer, SlideControls,InputSignUp,LogInLabel,
//         SignupLabel,InputLogIn,FormInner,FormInnerForm,FormField,FormFieldInput,
//         PassLink,} from './elements2'

const FullContainer = styled.div`
    margin: 0;
    box-sizing: border-box;
    position: absolute;
    padding-top: 200px;
    padding-left: 50px;
    padding-right: 300px;
    align-items: center;
    z-index: 10;
`
const Wrapper = styled.div`
    margin: 0;
    box-sizing: border-box;
    overflow: hidden;
    max-width: 390px;
    background: #fff;
    padding: 30px;
    border-radius: 5px;
    box-shadow: 0px 15px 20px rgba(0,0,0,0.1);
    place-items: center;
`
const TitleText = styled.div`
    display: flex;
    width: 200%;
`
const Title = styled.div`
    width: 50%;
    font-size: 35px;
    font-weight: 600;
    text-align: center;
    transition: all 0.6s cubic-bezier(0.68,-0.55,0.265,1.55);
`
const InputSignUp = styled.input.attrs({type: "radio"})`
    color: #000;
    display: none;

    &:checked{
        color: #fff;
        cursor: default;
        user-select: none;
    }
`
const InputLogIn = styled.input.attrs({type: "radio"})`
    color: #000;
    display: none;

    &:checked{
        color: #fff;
        cursor: default;
        user-select: none;
    }
`
const SignupLabel = styled.label`
    margin: 0;
    box-sizing: border-box;
    color: #000000;

    ${InputSignUp}:checked & {
        color: #fff;
        cursor: default;
        user-select: none;
    }

    ${InputLogIn}:checked & {
        color: #000;
    }
`
const LogInLabel = styled.label`
    margin: 0;
    box-sizing: border-box;
    color: #000000;

    ${InputSignUp}:checked & {
        color: #000;
    }

    ${InputLogIn}:checked & {
        color: #fff;
        cursor: default;
        user-select: none;
    }
`
const SlideControls = styled.div`
    margin: 0;
    box-sizing: border-box;
    position: relative;
    display: flex;
    height: 50px;
    width: 100%;
    overflow: hidden;
    margin: 30px 0 10px 0;
    justify-content: space-between;
    border: 1px solid lightgrey;
    border-radius: 5px;

    .slide{
        height: 100%;
        width: 100%;
        color: #fff;
        font-size: 18px;
        font-weight: 500;
        text-align: center;
        line-height: 48px;
        cursor: pointer;
        z-index: 1;
        transition: all 0.6s ease;
    }

    .slider-tab{
        position: absolute;
        height: 100%;
        width: 50%;
        left: 0;
        z-index: 0;
        border-radius: 5px;
        background: -webkit-linear-gradient(left, #a445b2, #fa4299);
        transition: all 0.6s cubic-bezier(0.68,-0.55,0.265,1.55);

        ${InputSignUp}:checked & {
            left: 50%;
        }
    }
`

const FormContainer = styled.div`
    margin: 0;
    box-sizing: border-box;
    width: 100%;
    overflow: hidden;
`
const FormInner = styled.div`
    margin: 0;
    box-sizing: border-box;
    display: flex;
    width: 200%;
`
const FormInnerForm = styled.form`
    margin: 0;
    box-sizing: border-box;
    width: 50%;
    transition: all 0.6s cubic-bezier(0.68,-0.55,0.265,1.55);
`
const FormField = styled.div`
    margin: 0;
    box-sizing: border-box;
    height: 50px;
    width: 100%;
    margin-top: 20px;
`
const FormFieldInput = styled.input`
    margin: 0;
    box-sizing: border-box;
    height: 100%;
    width: 100%;
    outline: none;
    padding-left: 15px;
    border-radius: 5px;
    border: 1px solid lightgrey;
    border-bottom-width: 2px;
    font-size: 17px;
    transition: all 0.3s ease;

    &:focus{
        border-color: #fc83bb;
    }

    &::placeholder{
        color: #999;
        transition: all 0.3s ease;
    }

    &:focus::placeholder{
        color: #b3b3b3;
    }
`
const PassLink = styled.div`
    margin: 0;
    box-sizing: border-box;
    margin-top: 5px;
`
const SignUpLink = styled.div`
    margin: 0;
    box-sizing: border-box;
    text-align: center;
    margin-top: 30px;
`
const PassLinkA = styled.a`
    margin: 0;
    box-sizing: border-box;
    color: #fa4299;
    text-decoration: none;

    &:hover{
        text-decoration: underline;
    }
`
const SignUpLinkA = styled.a`
    margin: 0;
    box-sizing: border-box;
    color: #fa4299;
    text-decoration: none;

    &:hover{
        text-decoration: underline;
    }
`
const FormButton = styled.button`
    margin: 0;
    box-sizing: border-box;
    height: 50px;
    width: 100%;
    border-radius: 5px;
    position: relative;
    overflow: hidden;
`
const ButtonLayer = styled.div`
    margin: 0;
    box-sizing: border-box;
    height: 100%;
    width: 300%;
    position: absolute;
    left: -100%;
    background: -webkit-linear-gradient(right, #a445b2, #fa4299, #a445b2, #fa4299);
    border-radius: 5px;
    transition: all 0.4s ease;

    &:hover{
        left: 0;
    }
`
const FormButtonInput = styled.input`
    margin: 0;
    box-sizing: border-box;
    height: 100%;
    width: 100%;
    z-index: 1;
    position: relative;
    background: none;
    border: none;
    color: #fff;
    padding-left: 0;
    border-radius: 5px;
    font-size: 20px;
    font-weight: 500;
    cursor: pointer;
`

const LoginSignUpForm = () => {
    return (
        <FullContainer>
            <Wrapper>
                <TitleText>
                    <Title>
                        Login Form
                    </Title>
                    <Title>
                        Signup Form
                    </Title>
                </TitleText>
                <FormContainer>
                    <SlideControls>
                        <InputLogIn name="slide" id="login" checked />
                        <InputSignUp name="slide" id="signup" />
                        <LogInLabel for="login"  class="login">Login</LogInLabel>
                        <SignupLabel for="signup" className="slide" class="signup">Signup</SignupLabel>
                        <div className='slider-tab'></div>
                    </SlideControls>
                    <FormInner>
                        <FormInnerForm action="#" class="login">
                            <FormField>
                                <FormFieldInput type="text" placeholder="Email Address" required />
                            </FormField>
                            <FormField>
                                <FormFieldInput type="password" placeholder="Password" required />
                            </FormField>
                            <PassLink>
                                <PassLinkA href="google.com">Forgot password?</PassLinkA>
                            </PassLink>
                            <FormButton>
                                <ButtonLayer></ButtonLayer>
                                <FormButtonInput type="submit" value="Login" />
                            </FormButton>
                            <SignUpLink>
                                Not a member? <SignUpLinkA href="googl.com">Signup now</SignUpLinkA>
                            </SignUpLink>
                        </FormInnerForm>
                        <FormInnerForm action="#" class="signup">
                            <FormField>
                                <FormFieldInput type="text" placeholder="Email Address" required />
                            </FormField>
                            <FormField>
                                <FormFieldInput type="password" placeholder="Password" required />
                            </FormField>
                            <FormField>
                                <FormFieldInput type="password" placeholder="Confirm password" required />
                            </FormField>
                            <FormButton>
                                <ButtonLayer></ButtonLayer>
                                <FormButtonInput type="submit" value="Signup" />
                            </FormButton>
                        </FormInnerForm>
                    </FormInner>
                </FormContainer>
            </Wrapper>
        </FullContainer>
    );
};

export default LoginSignUpForm;
