import React from 'react'
import styled, {css} from 'styled-components';

const FullContainer = styled.div`
    margin: 0;
    box-sizing: border-box;
    position: absolute;
    padding-top: 200px;
    padding-left: 50px;
    padding-right: 300px;
    align-items: center;
    z-index: 10;
`
const Wrapper = styled.div`
    margin: 0;
    box-sizing: border-box;
    overflow: hidden;
    max-width: 390px;
    background: #e7e1b2;
    padding: 30px;
    border-radius: 5px;
    box-shadow: 0px 15px 20px rgba(0,0,0,0.1);
    place-items: center;
`
const TitleText = styled.div`
    display: flex;
    width: 200%;
`
const Title = styled.div`
    width: 50%;
    font-size: 35px;
    font-weight: 600;
    text-align: center;
    transition: all 0.6s cubic-bezier(0.68,-0.55,0.265,1.55);
`
const SlideControls = styled.div`
    margin: 0;
    box-sizing: border-box;
    position: relative;
    display: flex;
    height: 50px;
    width: 100%;
    overflow: hidden;
    margin: 30px 0 10px 0;
    justify-content: space-between;
    border: 1px #283618;
    border-radius: 5px;

    .slide{
        height: 100%;
        width: 100%;
        color: #fff;
        font-size: 18px;
        font-weight: 500;
        text-align: center;
        line-height: 48px;
        cursor: pointer;
        z-index: 1;
        transition: all 0.6s ease;
    }
    .signuplabel {
        ${props => props.id='signup' && css`
            color: #000;
        `}
    }
    .loginLabel {}

    .sliderTab{
        position: absolute;
        height: 100%;
        width: 50%;
        left: 0;
        z-index: 0;
        border-radius: 5px;
        background: -webkit-linear-gradient(left, #455c2b, #283618);
        transition: all 0.6s cubic-bezier(0.68,-0.55,0.265,1.55);
    }
    .signUpInput {
        display: none;

        &:checked ~ .sliderTab{
            left: 50%;
        }
        &:checked ~ .signuplabel {
            color: #fff;
            font-weight: bolder;
            cursor: default;
            user-select: none;
        }
        &:checked ~ .loginLabel {
            color: #000;
        }

    }

    .loginInput {
        display: none;

        &:checked ~ .signuplabel{
            color: #000;    
        }
        &:checked ~ .loginLabel{
            font-weight: bolder;
            cursor: default;
            user-select: none;
        }

    }

`
const FormContainer = styled.div`
    margin: 0;
    box-sizing: border-box;
    width: 100%;
    overflow: hidden;
`
const FormInner = styled.div`
    margin: 0;
    box-sizing: border-box;
    display: flex;
    width: 200%;
`
const FormInnerForm = styled.form`
    margin: 0;
    box-sizing: border-box;
    width: 50%;
    transition: all 0.6s cubic-bezier(0.68,-0.55,0.265,1.55);
`
const FormField = styled.div`
    margin: 0;
    box-sizing: border-box;
    height: 50px;
    width: 100%;
    margin-top: 20px;
`
const FormFieldInput = styled.input`
    margin: 0;
    box-sizing: border-box;
    height: 100%;
    width: 100%;
    outline: none;
    padding-left: 15px;
    border-radius: 5px;
    border: 1px solid lightgrey;
    border-bottom-width: 2px;
    font-size: 17px;
    transition: all 0.3s ease;

    &:focus{
        border-color: #b02929;
    }

    &::placeholder{
        color: #999;
        transition: all 0.3s ease;
    }

    &:focus::placeholder{
        color: #b3b3b3;
    }
`
const PassLink = styled.div`
    margin: 0;
    box-sizing: border-box;
    margin-top: 5px;
`
const SignUpLink = styled.div`
    margin: 0;
    box-sizing: border-box;
    text-align: center;
    margin-top: 30px;
`
const PassLinkA = styled.a`
    margin: 0;
    box-sizing: border-box;
    color: #b02929;
    text-decoration: none;

    &:hover{
        text-decoration: underline;
    }
`
const SignUpLinkA = styled.a`
    margin: 0;
    box-sizing: border-box;
    color: #b02929;
    font-weight: bolder;
    text-decoration: none;

    &:hover{
        text-decoration: underline;
    }
`
const FormButton = styled.button`
    margin: 0;
    box-sizing: border-box;
    height: 50px;
    width: 100%;
    border-radius: 5px;
    position: relative;
    overflow: hidden;
    margin-top: 10px;
`
const ButtonLayer = styled.div`
    margin: 0;
    box-sizing: border-box;
    height: 100%;
    width: 300%;
    position: absolute;
    left: -100%;
    background: -webkit-linear-gradient(right,#455c2b, #283618);
    border-radius: 5px;
    transition: all 0.4s ease;

    &:hover{
        left: 0;
    }
`
const FormButtonInput = styled.input`
    margin: 0;
    box-sizing: border-box;
    height: 100%;
    width: 100%;
    z-index: 1;
    position: relative;
    background: none;
    border: none;
    color: #fff;
    padding-left: 0;
    border-radius: 5px;
    font-size: 20px;
    font-weight: bolder;
    cursor: pointer;
`
const LoginSignUpForm = () => {
    return (
        <FullContainer>
            <Wrapper>
                <TitleText>
                    <Title>
                        Login Form
                    </Title>
                    <Title>
                        Signup Form
                    </Title>
                </TitleText>
                <FormContainer>
                    <SlideControls>
                        <input type="radio" className='loginInput' name="slide" id="login" checked />
                        <input type="radio" className='signUpInput' name="slide" id="signup" />
                        <label for="login" className="slide loginLabel" id="login2">Login</label>
                        <label for="signup" className="slide signuplabel" id="signup2">Signup</label>
                        <div className="sliderTab"></div>
                    </SlideControls>
                    <FormInner>
                        <FormInnerForm action="#" id="login2">
                            <FormField>
                                <FormFieldInput type="text" placeholder="Email Address" required />
                            </FormField>
                            <FormField>
                                <FormFieldInput type="password" placeholder="Password" required />
                            </FormField>
                            <PassLink>
                                <PassLinkA href="google.com">Forgot password?</PassLinkA>
                            </PassLink>
                            <FormButton>
                                <ButtonLayer></ButtonLayer>
                                <FormButtonInput type="submit" value="Login" />
                            </FormButton>
                            <SignUpLink>
                                Not a member? <SignUpLinkA href="googl.com">Signup now</SignUpLinkA>
                            </SignUpLink>
                        </FormInnerForm>
                        <FormInnerForm action="#" id="signup2">
                            <FormField>
                                <FormFieldInput type="text" placeholder="Email Address" required />
                            </FormField>
                            <FormField>
                                <FormFieldInput type="password" placeholder="Password" required />
                            </FormField>
                            <FormField>
                                <FormFieldInput type="password" placeholder="Confirm password" required />
                            </FormField>
                            <FormButton>
                                <ButtonLayer></ButtonLayer>
                                <FormButtonInput type="submit" value="Signup" />
                            </FormButton>
                        </FormInnerForm>
                    </FormInner>
                </FormContainer>
            </Wrapper>
        </FullContainer>
    );
};

export default LoginSignUpForm;
